#include <iostream>
#include <conio.h>
using namespace std;

void showMenu();
void printPortrait();
void printJackMa();
void printhdu();
void printbutterfly();
void WhiteHouse();
void printCouleeNazhat();
void printPikachu();

int main(int argc, char *argv[])
{
  int choice = 0;

  while(choice != 8)
  {
      showMenu();

      cout<<"\tChoose Option: ";

      // If the input is wrong
      while( !(cin>>choice) || (choice<1) || (choice>8) )
      {
          cin.clear();
          cin.sync();
          cout<<"\tINVALID INPUT. Please try again.";
      }

      // Choose 8 to stop the choice loop
      if(choice == 8)
      {
          break;
      }

      switch(choice)
      {
          case 1:
              cout<<"Printing girl portrait:"<<endl;
              printPortrait();
              break;
          case 2:
              cout<<"Printing JackMa:"<<endl;
              printJackMa();
              break;
          case 3:
              cout<<"Printing HDU Logo:"<<endl;
              printhdu();
              break;
          case 4:
              cout<<"Printing butterfly:"<<endl;
              printbutterfly();
              break;
          case 5:
              cout<<"White House"<<endl;
              WhiteHouse();
              break;
          case 6:
              cout<<"Printing CouleeNazhat:"<<endl;
              printCouleeNazhat();
              break;
          case 7:
              cout<<"Printing Pikachu:"<<endl;
              printPikachu();
              break;
      }

      cout<<"\tYour choice is: "<<choice<<endl;
      cin.sync();

      if(choice != 8)
      {
          cout<<"\tPress Enter key to return to the menu.";
      }
      getchar();
  
  }
  return 0;
}

